import{_ as e,c as t,o as d,a4 as o}from"./chunks/framework.qqx5a1c4.js";const _=JSON.parse('{"title":"Client Config File","titleTemplate":"Simple Voice Chat","description":"","frontmatter":{"titleTemplate":"Simple Voice Chat","aside":false},"headers":[],"relativePath":"minecraft/voicechat/wiki/client_config.md","filePath":"minecraft/voicechat/wiki/client_config.md"}'),c={name:"minecraft/voicechat/wiki/client_config.md"},r=o('<h1 id="client-config-file" tabindex="-1">Client Config File <a class="header-anchor" href="#client-config-file" aria-label="Permalink to &quot;Client Config File&quot;">â€‹</a></h1><p><strong>Fabric/NeoForge/Forge/Quilt</strong></p><p><code>config/voicechat/voicechat-client.properties</code></p><table><thead><tr><th>Config key</th><th>Description</th><th>Default value</th></tr></thead><tbody><tr><td><code>onboarding_finished</code></td><td>If the voice chat onboarding process has been finished<br>See <a href="client_setup">this</a> for more information</td><td><code>false</code></td></tr><tr><td><code>voice_chat_volume</code></td><td>The voice chat volume</td><td><code>1.0</code></td></tr><tr><td><code>voice_activation_threshold</code></td><td>The threshold for voice activation in dB</td><td><code>-50.0</code></td></tr><tr><td><code>microphone_amplification</code></td><td>The voice chat microphone amplification</td><td><code>1.0</code></td></tr><tr><td><code>microphone_activation_type</code></td><td>Microphone activation type<br>Possible values are <code>PTT</code> and <code>VOICE</code></td><td><code>PTT</code></td></tr><tr><td><code>output_buffer_size</code></td><td>The size of the audio output buffer in packets<br>Higher values mean a higher latency, but less crackles<br>Increase this value if you have an unstable internet connection</td><td><code>5</code></td></tr><tr><td><code>audio_packet_threshold</code></td><td>The maximum amount of audio packets that are held back if a packet arrives out of order or gets dropped<br>This prevents discarding audio packets that are slightly out of order<br>Set this to <code>0</code> to disable</td><td><code>3</code></td></tr><tr><td><code>voice_deactivation_delay</code></td><td>The time it takes for the microphone to deactivate when using voice activation<br>A value of 1 means 20 milliseconds, 2=40 ms, 3=60 ms, ...</td><td><code>25</code></td></tr><tr><td><code>microphone</code></td><td>The microphone used by the voice chat<br>Empty for default device</td><td><code></code></td></tr><tr><td><code>speaker</code></td><td>The speaker used by the voice chat<br>Empty for default device</td><td><code></code></td></tr><tr><td><code>muted</code></td><td>If the microphone is muted (only when using voice activation)</td><td><code>true</code></td></tr><tr><td><code>disabled</code></td><td>If the voice chat is disabled (sound and microphone off)</td><td><code>false</code></td></tr><tr><td><code>hide_icons</code></td><td>If the voice chat icons should be hidden</td><td><code>false</code></td></tr><tr><td><code>show_group_hud</code></td><td>If the group HUD should be visible</td><td><code>true</code></td></tr><tr><td><code>show_own_group_icon</code></td><td>If the own icon should be shown when in a group</td><td><code>true</code></td></tr><tr><td><code>group_hud_icon_scale</code></td><td>The scale of the group HUD</td><td><code>2.0</code></td></tr><tr><td><code>group_player_icon_orientation</code></td><td>The orientation of the player icons in the group HUD<br>Possible values are <code>VERTICAL</code> and <code>HORIZONTAL</code></td><td><code>VERTICAL</code></td></tr><tr><td><code>group_player_icon_pos_x</code></td><td>The X position of the player icons in the group HUD<br>Negative values mean anchoring to the right</td><td><code>4</code></td></tr><tr><td><code>group_player_icon_pos_y</code></td><td>The Y position of the player icons in the group HUD<br>Negative values mean anchoring to the bottom</td><td><code>4</code></td></tr><tr><td><code>hud_icon_pos_x</code></td><td>The X position of the HUD icons<br>Negative values mean anchoring to the right</td><td><code>16</code></td></tr><tr><td><code>hud_icon_pos_y</code></td><td>The Y position of the HUD icons<br>Negative values mean anchoring to the bottom</td><td><code>-16</code></td></tr><tr><td><code>hud_icon_scale</code></td><td>The scale of the HUD icons</td><td><code>1.0</code></td></tr><tr><td><code>recording_destination</code></td><td>The location where recordings should be saved<br>Leave empty for default location</td><td><code></code></td></tr><tr><td><code>recording_quality</code></td><td>The quality of the recorded audio<br>0 = highest quality, 9 = lowest quality</td><td><code>2</code></td></tr><tr><td><code>denoiser</code></td><td>If noise cancellation should be enabled</td><td><code>false</code></td></tr><tr><td><code>run_local_server</code></td><td>If voice chat should work in singleplayer/LAN worlds</td><td><code>true</code></td></tr><tr><td><code>java_microphone_implementation</code></td><td>Whether to use the Java implementation of microphone capturing instead of OpenAL</td><td><code>true</code> for MacOS, <code>false</code> for every other OS</td></tr><tr><td><code>macos_check_microphone_permission</code></td><td>If the mod should check for microphone permissions (MacOS only)</td><td><code>true</code></td></tr><tr><td><code>show_fake_players_disconnected</code></td><td>If fake players should have the disconnected icon above their head</td><td><code>false</code></td></tr><tr><td><code>offline_player_volume_adjustment</code></td><td>If the volume adjustment GUI should also show offline players</td><td><code>false</code></td></tr><tr><td><code>audio_type</code></td><td>The 3D audio type<br>Possible values are <code>NORMAL</code>, <code>REDUCED</code> and <code>OFF</code></td><td><code>NORMAL</code></td></tr><tr><td><code>use_natives</code></td><td>If the mod should load native libraries<br>If set to false, the Java Opus implementation will be used, the denoiser won&#39;t be available and you won&#39;t be able to record audio.</td><td><code>true</code></td></tr><tr><td><code>freecam_mode</code></td><td>This setting was added in version 2.5<br>How listening to other players should work when using freecam mods<br>Possible values:<br><code>CAMERA</code> You will hear voice chat audio around your camera. Whether you hear distant audio depends on the voice chat broadcast range of the server<br><code>PLAYER</code> You will hear voice chat audio around your player no matter where your camera is<br>See <a href="freecam_mode">this</a> for more information</td><td><code>CAMERA</code></td></tr><tr><td><code>freecam_support</code></td><td>This setting is replaced with <code>freecam_mode</code> starting with version 2.5<br>This lets you hear players near you, even though you are further away with your freecam<br>See <a href="freecam_support">this</a> for more information</td><td><code>true</code></td></tr><tr><td><code>mute_on_join</code></td><td>If enabled, you will be automatically muted when joining a world</td><td></td></tr></tbody></table>',4),i=[r];function a(n,s,h,l,u,f){return d(),t("div",null,i)}const m=e(c,[["render",a]]);export{_ as __pageData,m as default};
