import{_ as e,c as t,o as d,a4 as o}from"./chunks/framework.qqx5a1c4.js";const u=JSON.parse('{"title":"Server Config File","titleTemplate":"Simple Voice Chat","description":"","frontmatter":{"titleTemplate":"Simple Voice Chat","aside":false},"headers":[],"relativePath":"minecraft/voicechat/wiki/server_config.md","filePath":"minecraft/voicechat/wiki/server_config.md"}'),c={name:"minecraft/voicechat/wiki/server_config.md"},r=o('<h1 id="server-config-file" tabindex="-1">Server Config File <a class="header-anchor" href="#server-config-file" aria-label="Permalink to &quot;Server Config File&quot;">â€‹</a></h1><p><strong>Fabric/NeoForge/Forge/Quilt</strong></p><p><code>config/voicechat/voicechat-server.properties</code></p><p><strong>Bukkit/Spigot/Paper</strong></p><p><code>plugins/voicechat/voicechat-server.properties</code></p><table><thead><tr><th>Config key</th><th>Description</th><th>Default value</th></tr></thead><tbody><tr><td><code>port</code></td><td>The port of the voice chat server<br>Setting this to <code>-1</code> sets the port to the Minecraft servers port (Not recommended)</td><td><code>24454</code></td></tr><tr><td><code>bind_address</code></td><td>The IP address to bind the voice chat server on<br>Leave empty to use <code>server-ip</code> of server.properties<br>To bind to the wildcard address, use <code>*</code></td><td><code></code></td></tr><tr><td><code>max_voice_distance</code></td><td>The distance to where the voice can be heard</td><td><code>48</code></td></tr><tr><td><code>crouch_distance_multiplier</code></td><td>The multiplier of the voice distance when crouching</td><td><code>1.0</code></td></tr><tr><td><code>whisper_distance_multiplier</code></td><td>The multiplier of the voice distance when whispering</td><td><code>0.5</code></td></tr><tr><td><code>codec</code></td><td>The opus codec<br>Possible values are <code>VOIP</code>, <code>AUDIO</code> and <code>RESTRICTED_LOWDELAY</code></td><td><code>VOIP</code></td></tr><tr><td><code>mtu_size</code></td><td>The maximum size in bytes that voice packets are allowed to have</td><td><code>1024</code></td></tr><tr><td><code>keep_alive</code></td><td>The frequency at which keep alive packets are sent<br>Setting this to a higher value may result in timeouts</td><td><code>1000</code></td></tr><tr><td><code>enable_groups</code></td><td>If group chats are allowed</td><td><code>true</code></td></tr><tr><td><code>voice_host</code></td><td>The host name that clients should use to connect to the voice chat<br>This may also include a port, e.g. &#39;example.com:24454&#39;<br>Don&#39;t change this value if you don&#39;t know what you are doing</td><td><code></code></td></tr><tr><td><code>allow_recording</code></td><td>If players are allowed to record the voice chat</td><td><code>true</code></td></tr><tr><td><code>spectator_interaction</code></td><td>If spectators are allowed to talk to other players</td><td><code>false</code></td></tr><tr><td><code>spectator_player_possession</code></td><td>If spectators can talk to players they are spectating</td><td><code>false</code></td></tr><tr><td><code>force_voice_chat</code></td><td>If players without the mod should get kicked from the server</td><td><code>false</code></td></tr><tr><td><code>login_timeout</code></td><td>The amount of milliseconds, the server should wait to check if the player has the mod installed<br>Only active when force_voice_chat is set to true</td><td><code>10 000</code></td></tr><tr><td><code>broadcast_range</code></td><td>The range where the voice chat should broadcast audio to<br>A value &lt;0 means <code>max_voice_distance</code></td><td><code>-1.0</code></td></tr><tr><td><code>allow_pings</code></td><td>If the voice chat server should reply to pings</td><td><code>true</code></td></tr></tbody></table>',6),a=[r];function i(s,h,n,l,p,_){return d(),t("div",null,a)}const f=e(c,[["render",i]]);export{u as __pageData,f as default};
